import React, { useState } from "react";
import "@fontsource/roboto/400.css";
// import { Typography } from "@mui/material";
function BaseRoom() {
  const [inputMessage, setInputMessage] = useState("");
  const [showMessage, setshowMessage] = useState("");
  const handleInput = (e) => {
    setInputMessage(e.target.value);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    setshowMessage(inputMessage);
    setInputMessage("");
  };

  return (
    <>
      <header className="flex justify-center">
        <p> Welcome to my real-time chat room</p>
      </header>
      <div className="border flex-col items-center flex h-80 p-2">
        <div className="p-2 m-2 h-20 w-44">
          <p>Public Message</p>
        </div>
        <div className="flex border w-full h-full justify-center mt-2">
          <p>{showMessage}</p>
        </div>
      </div>
      <div className="flex flex-col justify-center  items-center h-60 p-4">
        <p>Input Text</p>
        <form onSubmit={handleSubmit}>
          <input
            value={inputMessage}
            onChange={handleInput}
            className="border w-60 h-32"
            type="text"
          ></input>
        </form>
      </div>
    </>
  );
}
export default BaseRoom;
